audition = Audition.create!(name_of_company: "iambe theatre ensemble",  union_status: "Non-Equity", pay_scale: "$100/Stipend", name_of_show: "Good People", written_by: "David Lindsay-Abaire", director: "Annie Slivinski", musical_director: nil, choreographer: nil, rehearsal_startdate: "2024-09-16", tech_startdate: "2024-10-20", previews_startdate: nil, show_startdate: "2024-10-25", show_enddate: "2024-11-10", show_address_id: show_address.id, show_warnings: "", show_description: "Welcome to Southie, a Boston neighborhood where a night on the town means a few rounds of bingo, where this month's paycheck covers last month's bills, and where Margie Walsh has just been let go from yet another job. Facing eviction and scrambling to catch a break, Margie thinks an old fling who's made it out of Southie might be her ticket to a fresh start. But is this apparently self-made man secure enough to face his humble beginnings? Margie is about to risk what little she has left to find out. With his signature humorous glow, Lindsay-Abaire explores the struggles, shifting loyalties and unshakeable hopes that come with having next to nothing in America.", additional_notes: nil, audition_address_id: , audition_date1: "2024-06-23", audition_time1: "6:00PM-9:00PM", audition_date2: "2024-06-24", audition_time2: "7:00PM-9:00PM", audition_date3: nil, audition_time3: nil, audition_date4: nil, audition_time4: nil, audition_date5: nil, audition_time5: nil, callback_address_id: , callback_date1: "", callback_time1: "", callback_date2: nil, callback_time2: nil, callback_date3: nil, callback_time3: nil, in_the_room: nil, materials_to_prepare: "Actors will be asked to read sides from the script.", how_to_submit: "Fill out the audition form here: https://docs.google.com/forms/d/e/1FAIpQLScuCPrGUaXZsaBZfGRlGMdzGsntFdKjnwPwU58f1KA6zhd06g/viewform")

