audition = Audition.create!(name_of_company: "Glass Apple Theatre",  union_status: "Non-Equity", pay_scale: "$1,600 paid weekly in installments of $200 per week. Depending on availability there may be some preliminary script work during the week prior to rehearsals starting on Monday July 8th. If so, performers will be additionally compensated.", name_of_show: "This One Will Grow", written_by: "", director: nil, musical_director: nil, choreographer: nil, rehearsal_startdate: "2024-07-10", tech_startdate: "2024-07-29", previews_startdate: nil, show_startdate: "2024-08-01", show_enddate: "2024-09-01", show_address_id: nil, show_warnings: "The show contains strong language and discussions including sex work, drug abuse, and addiction, none of which are depicted onstage", show_description: "A mother and her three daughters are together again under one roof for the first time in years, but the combustible nature of their personalities brings to the surface years of simmering resentments and slights and threatens to destroy their last chance at reconciliation. This is a world premiere.", additional_notes: nil, audition_address_id: , audition_date1: "2024-06-07", audition_time1:"10:00PM", audition_date2: "", audition_time2: "", audition_date3: nil, audition_time3: nil, audition_date4: nil, audition_time4: nil, audition_date5: nil, audition_time5: nil, callback_address_id: , callback_date1: "2024-06-10", callback_time1: "", callback_date2: nil, callback_time2: nil, callback_date3: nil, callback_time3: nil, in_the_room: nil, materials_to_prepare: "Please tape a 1 min contemporary monologue of any genre for the role you feel most suited for. The tone of the show is a drama with some quirky, eccentric characters. Don't be afraid to show a sense of humor if it works in the piece.", how_to_submit: "Videos should be unlisted YouTube/Video files. Send the link to the file, along with a PDF of your headshot and resume to brian@glassappletheatre.com no later than 10pm CST Friday June 7th, 2024. The subject line of the email should read 'GROW Submission - [Name of Role]")

Character.create!(audition_id: audition.id, name: "Joyce", age: "50s", gender: "Female", ethnicity: "Any/Open", description: "Joyce works at a big box store, and she is desperate to atone for past mistakes even if that means alienating the two daughters living in her home in favor of the daughter she feels she neglected. She is feisty and stubborn, with a nurturing nature that has been blunted by years of disappointments and sadness.", skills: "", notes: nil)
Character.create!(audition_id: audition.id, name: "Lisa", age: "30s", gender: "Female", ethnicity: "Any/Open", description: "A no-nonsense woman who has shouldered the thankless burden of keeping the family going despite her personal disdain for her sister ANNE, and the generation gap that separates her from her youngest sister JESSICA. She works as the assistant manager for a fast-food restaurant.", skills: "", notes: nil)
Character.create!(audition_id: audition.id, name: "Anne", age: "Mid-20s", gender: "Female", ethnicity: "Any/Open", description: "A recovering addict, much of the family's present tensions stem from their differing views of her struggles. She is visiting her mother and sisters from her normal living situation, a full-time recovery residence. She is embarrassed and filled with regret for her chaotic history, but hopeful that she can put her life back on track.", skills: "", notes: nil)
Character.create!(audition_id: audition.id, name: "Jessica", age: "20s", gender: "Female", ethnicity: "Any/Open", description: "Her lucrative gig on the website VIP Girls is a closely held secret but the money she gets from the subscription only service is becoming harder to hide. She is fiercely independent and bristles at her sisters' judgmental attitudes.", skills: "", notes: nil)


