audition = Audition.create!(name_of_company: "Gwydion Theatre Company",  union_status: "Non-Equity", pay_scale: "$150 Stipend", name_of_show: "This Is Our Youth", written_by: "", director: nil, musical_director: nil, choreographer: nil, rehearsal_startdate: "2024-08-05", tech_startdate: "2024-08-31", previews_startdate: nil, show_startdate: "2024-09-05", show_enddate: "2024-09-28", show_address_id: nil, show_warnings: "", show_description: "Set in New York in 1982, This Is Our Youth follows forty-eight hours in the lives of three very lost young souls: Warren, a dejected nineteen year old who has just stolen $15,000 from his abusive, tycoon father; Dennis, his charismatic drug-dealing friend who helps Warren put the stolen money to good use and Jessica, an anxiously insightful girl who Warren yearns for.", additional_notes: nil, audition_address_id: audition_address.id , audition_date1: "2024-06-29", audition_time1: "4:00PM-8:00PM", audition_date2: "2024-06-30", audition_time2: "10:00AM-6:00PM", audition_date3: nil, audition_time3: nil, audition_date4: nil, audition_time4: nil, audition_date5: nil, audition_time5: nil, callback_address_id: , callback_date1: "2024-07-06", callback_time1: "12:00PM-6:00PM", callback_date2: nil, callback_time2: nil, callback_date3: nil, callback_time3: nil, in_the_room: nil, materials_to_prepare: "Please prepare a contemporary monologue not to exceed two minutes", how_to_submit: "To secure a slot email your headshot and resume to gwydiontheatrecompany@gmail.com alongside your preferred day/time.")

Character.create!(audition_id: audition.id, name: "Jessica Goldman", age: "19", gender: "Female", ethnicity: "Any/Open", description: "Nineteen-year-old Jessica Goldman is a "cheerful but very nervous girl" who displays "a watchful defensiveness that sweeps away anything that might threaten to dislodge her, including her own chances at happiness and the opportunity of gaining a wider perspective on the world." She uses this defensiveness to help her project her own image of herself as a hip, intelligent, independent young woman who cannot be taken advantage of, yet her actions suggest that she is not as self-assured as she appears.", skills: "Character requires stage intimacy", notes: nil)


