audition = Audition.create!(name_of_company: "City Lit Theater",  union_status: "Non-Equity", pay_scale: "$100 rehearsal stipend, plus $20 per performance (23 performances); total compensation $560.", name_of_show: "Seven Guitars", written_by: "August Wilson", director: nil, musical_director: nil, choreographer: nil, rehearsal_startdate: "2024-09-05", tech_startdate: nil, previews_startdate: nil, show_startdate: "2024-10-18", show_enddate: "2024-12-01", show_address_id: show_address.id, show_warnings: "", show_description: "The Chicago storefront premiere of this searing August Wilson drama about six friends in the Hill District of Pittsburgh in 1948 mourning the death of their friend Floyd "Schoolboy" Barton, a rising blues star trapped by his circumstances and cut down in his prime. One of Wilson's "Pittsburgh Cycle" of ten plays depicting the African American experience in each of the 20th Century's decades.", additional_notes: nil, audition_address_id: audition_address.id , audition_date1: "2024-06-22", audition_time1: "12:00PM-5:00PM", audition_date2: "2024-06-23", audition_time2: "1:00PM-5:00PM", audition_date3: nil, audition_time3: nil, audition_date4: nil, audition_time4: nil, audition_date5: nil, audition_time5: nil, callback_address_id: , callback_date1: "", callback_time1: "", callback_date2: nil, callback_time2: nil, callback_date3: nil, callback_time3: nil, in_the_room: nil, materials_to_prepare: "Audition will consist of sides that will be emailed upon request. Please bring headshot/resume with you to the audition.", how_to_submit: "Please email auditions@citylit.org")

Character.create!(audition_id: audition.id, name: "Floyd", age: "Mid 20s-30s", gender: "Male", ethnicity: "Black/African American", description: "Floyd 'Schoolboy' Barton, a down on his luck blues musician. Had 1 hit record, but squandered the money from it. After being jailed for vagrancy, and spending 90 days in the workhouse, he hopes to return to Chicago & rekindle his music career.", skills: "", notes: nil)
Character.create!(audition_id: audition.id, name: "Vera", age: "Mid 20-30s", gender: "", ethnicity: "Black/African American", description: "Formerly romantically attached to Floyd. Floyd cheated on Vera in the past, causing her to mistrust him-and probably any other man. But that doesn't stop Floyd from trying to rekindle their relationship.", skills: "", notes: nil)
