Character.create!(audition_id: audition.id, name: "Canewell", age: "Mid 20-30s", gender: "Male", ethnicity: "Black/African American", description: "Edgy, quick tempered. He is a harmonica player. May have courted Vera, if not for Floyd dating her first. He's tired of playing second fiddle to Floyd, so to speak.", skills: "", notes: nil)
Character.create!(audition_id: audition.id, name: "Hedley", age: "40-60s", gender: "Male", ethnicity: "Black/African American", description: "A Caribbean immigrant who rents a room from Vera. He raises chickens in the yard. He believes in spirits, and ghosts.", skills: "", notes: nil)
Character.create!(audition_id: audition.id, name: "Ruby", age: "Early 20s", gender: "Female", ethnicity: "Black/African American", description: "Louise's niece in town from Alabama. Pregnant, not showing. Her boyfriend back home killed her would-be lover.", skills: "", notes: nil)
