audition = Audition.create!(name_of_company: "Parker Players Theater Company",  union_status: "Non-Equity", pay_scale: "$200", name_of_show: "Amadeus", written_by: "Peter Shaffer", director: "Chris Causer and Jenilee Houghton", musical_director: nil, choreographer: nil, rehearsal_startdate: "2024-05-06", tech_startdate: "2024-07-07", previews_startdate: nil, show_startdate: "2024-07-12", show_enddate: "2024-07-28", show_address_id: show_address.id, show_warnings: "", show_description: "Parker Players Theater Company is excited to announce auditions for Amadeus by Peter Shaffer. Directors Chris Causer and Jenilee Houghton are bringing a rebellious new take on this classic story. It’s a tale of two artists and how their ego-driven battle for fame and glory ultimately leads to their respective demise. A levity-filled drama whose scope is as operatic as it comes, but presented as an intimate, immersive (and slightly anarchic) new experience.", additional_notes: nil, audition_address_id: audition_address.id, audition_date1: "2024-04-14", audition_time1: "1:00PM-4:00PM", audition_date2: "2024-04-15", audition_time2: "6:00PM-9:00PM", audition_date3: nil, audition_time3: nil, audition_date4: nil, audition_time4: nil, audition_date5: nil, audition_time5: nil, callback_address_id: callback_address.id, callback_date1: "2024-04-16", callback_time1: "6:00PM-10:00PM", callback_date2: nil, callback_time2: nil, callback_date3: nil, callback_time3: nil, in_the_room: nil, materials_to_prepare: "Please prepare a 1-2 minute monologue OR prepare one of the selected excerpts from the play. Play excerpts can be found here: https://docs.google.com/document/d/1tYn1T_bx4W9GBRqYxTSJfrAtpUzPyLk-pYX5utjZnos/edit?usp=sharing If selecting one of the provided monologues from the show, feel free to choose any option that you connect with, regardless of character. We also welcome singers and musicians to share up to 60 seconds of an operatic or punk/rock piece of music. Keyboard will be provided, bu