audition = Audition.create!(name_of_company: "Refracted Theatre Company",  union_status: "Non-Equity", pay_scale: "$250 per week during rehearsals, $200 per week during performances. Pay for understudies: $75-$100 per week during rehearsals, $50 per week during performances.", name_of_show: "Coronation", written_by: "Laura Winters", director: nil, musical_director: nil, choreographer: nil, rehearsal_startdate: "2024-08-26", tech_startdate: "2024-10-04", previews_startdate: "2024-10-11", show_startdate: "2024-10-18", show_enddate: "2024-11-16", show_address_id: nil, show_warnings: "", show_description: "A collaboration years in the making, Refracted is thrilled to be producing CORONATION by Laura Winters. A femme-futurist political satire, this production will challenge all of your notions around storefront theater in Chicago, as you double over in laughter and tremble with fear. After another female candidate loses the presidency, three women drunkenly invent a way to circumvent elections and place a woman in power: all hail the Queen of America. The play positions female autonomy against the power and prestige of artificial intelligence, as it asks scarily topical questions like: can we work within a system that was never meant to work for us? When it comes to the government, AI, and the public, who works for whom?", additional_notes: nil, audition_address_id: audition_address.id, audition_date1: "2024-04-07", audition_time1: "10:00AM-1:00PM", audition_date2: "2024-04-09", audition_time2: "5:00PM-10:00PM", audition_date3: nil, audition_time3: nil, audition_date4: nil, audition_time4: nil, audition_date5: nil, audition_time5: nil, callback_address_id: , callback_date1: "", callback_time1: "", callback_date2: nil, callback_time2: nil, callback_date3: nil, callback_time3: nil, in_the_room: nil, materials_to_prepare: "Sides will be provided upon scheduling auditions.", how_to_submit: "Auditions will be by invitation based on hs/res submissions sent through https://drive.google.com/drive/folders/1GSxDrIg4rRYm-urkLdHlcnp51ByXjnoo")

